    private void topLeftActionPerformed(java.awt.event.ActionEvent evt) {                                        
       if(counter%2==0)
       {
           topLeft.setText("X");
           board[0][0]=1;
           topLeft.setForeground(Color.RED);
       }
       else{
           topLeft.setText("O");
           board[0][0]=2;
           topLeft.setForeground(Color.BLACK);
       }
       counter++;
       check();
    }                                       

