    /**
     * @param args the command line arguments
     */
    private void check()
    {
        if((board[0][0]==1 && board[0][1]==1 && board[0][2]==1)||(board[1][0]==1 && board[1][1]==1 && board[1][2]==1)||(board[2][0]==1 && board[2][1]==1 && board[2][2]==1)||(board[0][0]==1 && board[1][1]==1 && board[2][2]==1)||(board[0][2]==1 && board[1][1]==1 && board[2][0]==1)||(board[0][0]==1 && board[1][0]==1 && board[2][0]==1)||(board[0][1]==1 && board[1][1]==1 && board[2][1]==1)||(board[0][2]==1 && board[1][2]==1 && board[2][2]==1))
        {
            JOptionPane.showMessageDialog(this,"X has won");reset();
        }
        if((board[0][0]==2 && board[0][1]==2 && board[0][2]==2)||(board[1][0]==2 && board[1][1]==2 && board[1][2]==2)||(board[2][0]==2 && board[2][1]==2 && board[2][2]==2)||(board[0][0]==2 && board[1][1]==2 && board[2][2]==2)||(board[0][2]==2 && board[1][1]==2 && board[2][0]==2)||(board[0][0]==2 && board[1][0]==2 && board[2][0]==2)||(board[0][1]==2 && board[1][1]==2 && board[2][1]==2)||(board[0][2]==2 && board[1][2]==2 && board[2][2]==2))
        {
            JOptionPane.showMessageDialog(this,"O has won");reset();
        }
        if(board[0][0]!=0 && board[0][1]!=0 && board[0][2]!=0 && board[1][0]!=0&&board[1][1]!=0&&board[1][2]!=0&&board[2][0]!=0&&board[2][1]!=0&&board[2][2]!=0)
        {
            JOptionPane.showMessageDialog(this,"Cats Game!");
            reset();
            
        }
    }
    private void reset()
    {
        counter=0;
        for(int i=0;i<board.length;i++)
            {
                for(int j=0;j<board.length;j++)
                {
                    board[i][j]=0;
                }
            }
        topLeft.setText("");
        topMiddle.setText("");
        topRight.setText("");
        middleLeft.setText("");
        middleMiddle.setText("");
        middleRight.setText("");
        bottomLeft.setText("");
        bottomMiddle.setText("");
        bottomRight.setText("");
        System.out.println("Board Reset!");
    }
